Here is a simple implementation of a linked list in C++:

```cpp
#include<iostream>
using namespace std;

class Node {
public:
    int data;
    Node* next;
    Node(int d) {
        data = d;
        next = NULL;
    }
};

class LinkedList {
private:
    Node* head;
public:
    LinkedList() {
        head = NULL;
    }

    void add(int d) {
        if (head == NULL) {
            head = new Node(d);
            return;
        }
        Node* temp = head;
        while (temp->next != NULL) {
            temp = temp->next;
        }
        temp->next = new Node(d);
        return;
    }

    void print() {
        Node* temp = head;
        while (temp != NULL) {
            cout << temp->data << " ";
            temp = temp->next;
        }
        cout << "\n";
    }

    void remove(int d) {
        if (head == NULL) return;
        Node* temp = head;
        if (head->data == d) {
            head = head->next;
            delete temp;
            return;
        }
        while (temp->next != NULL && temp->next->data != d) {
            temp = temp->next;
        }
        if (temp->next == NULL) return;
        Node* to_remove = temp->next;
        temp->next = temp->next->next;
        delete to_remove;
        return;
    }
};
