```java
import java.util.Arrays;

public class Permutations {
    public static void main(String[] args) {
        int[] array = {1, 2, 3};
        permutate(array, array.length);

    }
    private static void permutate(int[] array, int n) {
        if (n == 1) {
            System.out.println(Arrays.toString(array));
            return;
        }
        for (int i = 0; i < n; i++) {
            swap(array, i, n - 1);
            permutate(array, n - 1);
            swap(array, i, n - 1);
        }

    }
